package SeleniumBeginer;

public class PrintEvenNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;

		System.out.println("The Even no are:");

		for (i = 1; i <= 200; i++) {

			if (i % 2 == 0) {

				System.out.println(i);
			}

		}

	}

}
