package SeleniumBeginer;

public class PrintOddNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;

		System.out.println("The odd no are:");

		for (i = 1; i <= 50; i++) {

			if (i % 2 == 1) {

				System.out.println(i);
			}

		}

	}

}
